import React, { useState } from "react";
import "./styles.css";

const ConversionCalculator = () => {
  const [conversionType, setConversionType] = useState("kmToMi");
  const [inputValue, setInputValue] = useState("");
  const [result, setResult] = useState("");
  const [savedConversions, setSavedConversions] = useState([]);

  const handleConversionTypeChange = (event) => {
    setConversionType(event.target.value);
    setInputValue("");
    setResult("");
  };

  const handleInputChange = (event) => {
    const value = event.target.value;
    setInputValue(value);
    convertValue(value);
  };

  const convertValue = (value) => {
    if (value === "") {
      setResult("");
      return;
    }

    let convertedValue;
    let originalUnit, convertedUnit;

    switch (conversionType) {
      case "kmToMi":
        convertedValue = parseFloat(value) * 0.621371;
        originalUnit = "kilómetros";
        convertedUnit = "millas";
        break;
      case "miToKm":
        convertedValue = parseFloat(value) * 1.60934;
        originalUnit = "millas";
        convertedUnit = "kilómetros";
        break;
      case "ftToM":
        convertedValue = parseFloat(value) * 0.3048;
        originalUnit = "pies";
        convertedUnit = "metros";
        break;
      case "mToFt":
        convertedValue = parseFloat(value) * 3.28084;
        originalUnit = "metros";
        convertedUnit = "pies";
        break;
      case "cmToIn":
        convertedValue = parseFloat(value) * 0.393701;
        originalUnit = "centímetros";
        convertedUnit = "pulgadas";
        break;
      case "inToCm":
        convertedValue = parseFloat(value) * 2.54;
        originalUnit = "pulgadas";
        convertedUnit = "centímetros";
        break;
      default:
        convertedValue = "";
        originalUnit = "";
        convertedUnit = "";
    }

    setResult(`${convertedValue.toFixed(2)} ${convertedUnit}`);
  };

  const saveConversion = () => {
    if (result !== "") {
      const conversion = {
        originalValue: inputValue,
        convertedValue: result,
        type: result,
      };

      setSavedConversions([...savedConversions, conversion]);
      setInputValue("");
      setResult("");
    }
  };

  const deleteConversion = (index) => {
    const updatedConversions = [...savedConversions];
    updatedConversions.splice(index, 1);
    setSavedConversions(updatedConversions);
  };

  return (
    <div>
      <h1 className="titulo">&#8646; unit converter</h1>
      <div className="container">
        <h2>convert</h2>
        <div className="input-container">
          <select value={conversionType} onChange={handleConversionTypeChange}>
            <option value="kmToMi">Kilómetros a Millas</option>
            <option value="miToKm">Millas a Kilómetros</option>
            <option value="ftToM">Pies a Metros</option>
            <option value="mToFt">Metros a Pies</option>
            <option value="cmToIn">Centímetros a Pulgadas</option>
            <option value="inToCm">Pulgadas a Centímetros</option>
          </select>
          <button onClick={swapValues}>&#8646;</button>
          <input type="text" value={inputValue} onChange={handleInputChange} />
        </div>
        <div className="result">{result}</div>
        <button className="save-button" onClick={saveConversion}>
          ❤
        </button>
      </div>
      <div className="container-conversion">
        <ul className="saved-conversions">
          {savedConversions.map((conversion, index) => (
            <li key={index} className="saved-conversion">
              <span className="saved-conversion-text">{conversion.type}</span>
              <button
                className="saved-conversion-delete"
                onClick={() => deleteConversion(index)}
              >
                X
              </button>
            </li>
          ))}
        </ul>
        <ul className="saved-conversions">
          {savedConversions.map((conversion, index) => (
            <li key={index} className="saved-conversion">
              <span className="saved-conversion-text">{conversion.type}</span>
              <button
                className="saved-conversion-delete"
                onClick={() => deleteConversion(index)}
              >
                X
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ConversionCalculator;
