import React, { useState, useEffect } from "react";
import "./styles.css";

const ConversionApp = () => {
  const [conversionType, setConversionType] = useState("kmToMiles");
  const [inputValue, setInputValue] = useState("");
  const [result, setResult] = useState("");
  const [savedConversions, setSavedConversions] = useState([]);

  const handleConversionTypeChange = (e) => {
    setConversionType(e.target.value);
    setInputValue("");
    setResult("");
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    const convertedValue = convert(value, conversionType);
    setResult(convertedValue);
  };

  const convert = (value, type) => {
    let resultValue;
    let conversionType;

    switch (type) {
      case "kmToMiles":
        resultValue = (value * 0.621371).toFixed(2);
        conversionType = "Millas";
        break;
      case "milesToKm":
        resultValue = (value * 1.60934).toFixed(2);
        conversionType = "Kilómetros";
        break;
      case "feetToMeters":
        resultValue = (value * 0.3048).toFixed(2);
        conversionType = "Metros";
        break;
      case "metersToFeet":
        resultValue = (value * 3.28084).toFixed(2);
        conversionType = "Pies";
        break;
      case "cmToInches":
        resultValue = (value * 0.393701).toFixed(2);
        conversionType = "Pulgadas";
        break;
      case "inchesToCm":
        resultValue = (value * 2.54).toFixed(2);
        conversionType = "Centímetros";
        break;
      default:
        resultValue = "";
        conversionType = "";
    }

    return {
      result: resultValue,
      type: conversionType,
    };
  };

  const saveConversion = () => {
    const { result: resultValue, type: conversionType } = result; // Desestructurar result

    const savedConversion = {
      type: conversionType,
      input: inputValue,
      result: resultValue,
    };

    setSavedConversions([...savedConversions, savedConversion]);
    setInputValue("");
    setResult("");
  };

  const deleteConversion = (index) => {
    const updatedConversions = [...savedConversions];
    updatedConversions.splice(index, 1);
    setSavedConversions(updatedConversions);
  };

  const swapConversion = () => {
    setInputValue(result.result);
    setResult(inputValue);
  };
  useEffect(() => {
    setResult(convert(inputValue, conversionType));
  }, [inputValue, conversionType]);

  return (
    <div className="container">
      <h1>convert</h1>
      <div className="content">
        <div className="selector">
          <select
            className="select"
            value={conversionType}
            onChange={handleConversionTypeChange}
          >
            <option value="kmToMiles">Kilómetros a Millas</option>
            <option value="milesToKm">Millas a Kilómetros</option>
            <option value="feetToMeters">Pies a Metros</option>
            <option value="metersToFeet">Metros a Pies</option>
            <option value="cmToInches">Centímetros a Pulgadas</option>
            <option value="inchesToCm">Pulgadas a Centímetros</option>
          </select>
          <button className="swap-button" onClick={swapConversion}>
            &#8646;
          </button>
          <input
            className="input"
            type="text"
            value={inputValue}
            onChange={handleInputChange}
          />
          <div className="result">
            {result.result && `${result.result} ${result.type}`}
          </div>
        </div>
        <div className="save-button-position">
          <button className="save-button" onClick={saveConversion}>
            <p>❤️</p>
          </button>
        </div>
      </div>
      <div>
        {" "}
        <h2>saved</h2>
        <ul>
          {savedConversions.map((conversion, index) => (
            <li key={index}>
              {conversion.input}{" "}
              {conversion.type === conversionType ? "=" : "↔"}{" "}
              {conversion.result}{" "}
              <button onClick={() => deleteConversion(index)}>X</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ConversionApp;
